const { default: styled } = require("styled-components");

export const Input = styled.input`
position: relative;
 box-sizing: border-box; 
width: 100%;
padding: 14px 15px;
border-radius: 6px;
background-color:var(--color-white);
border: 1px solid var(--border); 
margin-bottom: 25px;
`;
export const IconStyle = styled.button`
position: absolute;
left: 0px;
bottom: 10px;
`;
