import styled from 'styled-components';

const AppContainer = styled.section`
    width: 1500px;
    height: 970px;
    padding-left: 12px;
    padding-right: 12px;
    padding-top: 180px;
    text-align:center;
    background-color: var(--color-white);
    
    `;
const AppContainerWrap = styled.div`
    width: 400px;
margin: 0 auto;
    `;

export {AppContainerWrap,AppContainer}  ;
